/*Table structure for table `course_lesson` */

DROP TABLE IF EXISTS `course_lesson`;

CREATE TABLE `course_lesson` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `course_id` int(11) NOT NULL COMMENT '课程id',
  `section_id` int(11) NOT NULL DEFAULT '0' COMMENT '章节id',
  `theme` varchar(255) NOT NULL COMMENT '课时主题',
  `duration` int(11) NOT NULL DEFAULT '0' COMMENT '课时时长(分钟)',
  `is_free` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否免费',
  `start_img_url` varchar(255) DEFAULT NULL COMMENT '课时背景图',
  `text_content` mediumtext COMMENT '课时内容',
  `markdown_text_content` mediumtext COMMENT '课时内容（markdown文本）',
  `transcode` varchar(100) DEFAULT NULL COMMENT '转码集合',
  `resource_url` varchar(255) DEFAULT NULL COMMENT '课程资源路径',
  `last_operator_id` int(11) DEFAULT NULL COMMENT '最后操作者id',
  `create_time` datetime DEFAULT NULL COMMENT '记录创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `is_del` tinyint(1) DEFAULT '0' COMMENT '是否删除',
  `order_num` int(11) DEFAULT NULL COMMENT '排序字段',
  `ali_file_url` varchar(255) DEFAULT NULL COMMENT '阿里云视频文件URL',
  `ali_file_dk` varchar(255) DEFAULT NULL COMMENT '阿里云视频文件的DK',
  `ali_file_edk` varchar(255) DEFAULT NULL COMMENT '阿里云视频文件的EDK',
  `ali_file_vid` varchar(50) DEFAULT NULL COMMENT '阿里云视频资源文件ID',
  `status` int(2) DEFAULT NULL COMMENT '课时状态,0-隐藏，1-未发布，2-已发布',
  `is_timing_publish` tinyint(1) DEFAULT '0' COMMENT '是否定时发布',
  `publish_time` datetime DEFAULT '1970-01-01 08:00:00' COMMENT '定时发布时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `course_id_index` (`course_id`,`section_id`) USING BTREE,
  KEY `idx_sectionId_orderNum` (`section_id`,`order_num`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;

